import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styles from "./header.module.scss"

function Header(props) {
  const [windowPath, setWindowPath] = useState(window.location.pathname);

  useEffect(() => {
    setWindowPath(window.location.pathname);
  }, [window.location.pathname]);

  return (
    <header className={styles.header}>
      <Link to="/"><div className={styles.header__logo}></div></Link>
      <div>
        {props.isloggedIn ? (
          <div className={styles.header__menu}>
            <div>{props.email}</div>{" "}
            <button className={styles.header__link} onClick={props.handleLogout}>
              Выйти
            </button>
          </div>
        ) : windowPath === "/sign-up" ? (
          <Link to="/login" className={styles.header__link}>
            Login
          </Link>
        ) : (
          <Link to="/sign-up" className={styles.header__link}>
            Sign-up
          </Link>
        )}
      </div>
    </header>
  );
}

export default Header;
